import React from 'react';
import { ChatMessage as MessageType } from '@un360/core';

interface ChatMessageProps {
  message: MessageType;
  theme: any;
}

export const ChatMessage: React.FC<ChatMessageProps> = ({ message, theme }) => {
  const isUser = message.sender === 'user';

  return (
    <div
      style={{
        display: 'flex',
        justifyContent: isUser ? 'flex-end' : 'flex-start',
        marginBottom: '12px',
      }}
    >
      <div
        style={{
          backgroundColor: isUser ? theme.primaryColor : '#f1f1f1',
          color: isUser ? theme.secondaryColor : '#333',
          padding: '10px 14px',
          borderRadius: '12px',
          maxWidth: '70%',
          wordBreak: 'break-word',
          fontSize: '14px',
          lineHeight: '1.4',
        }}
      >
        {message.text}
      </div>
    </div>
  );
};